package com.sofiane.repl02;

public class Exerice67NestedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        //Write a program to print out the pattern: 

        // $$$$$$
        // $$$$$$
        // $$$$$$
        // $$$$$$
		
		for(int i=1; i<=4; i++){
			System.out.print("$");
		
			for(int j=1; j<=5; j++) {
				System.out.print("$");
			}
			System.out.println();
			
		
		}
		}
}
		
	
